
def print_questions()
	puts "#------------------ Taper le numero de la question pour avoir la reponse ------------#"
	puts "1. Combien y a-t-il de journalistes dans ce array ?"
	puts "2. Combien d'handle contiennent un numéro ?"
	puts "3. Combien d'handle contiennent les 4 lettres du prénom ""aude"" à la suite ? (s'il y a ""Aude"", ou ""aUde"", ou ""AUDE"", ça doit compter)"
	puts "4. Combien commencent par une majuscule (première lettre juste après le @) ?"
	puts "5. Combien contiennent au moins une majuscule ?"
	puts "6. Combien y a-t-il de underscore _ dans tous les pseudos confondus ?"
	puts "7. Trie la liste de handle par ordre alphabétique."
	puts "8. Quels sont les 50 handles les plus courts de ce array ?"
	puts "9. Quelle est la position dans l'array de la personne @epenser ?"
end

def ask_questions(choise, data)
	case choise
	when 1
		puts "\nLa réponse à la question #{choise} est : "
		puts "Il y a #{data.length} journalistes dans ce array."
	when 2
		puts "\nLa reponse à la question #{choise} est : "
		tjNumber = []
		for journa in data
			tab = journa.chars
			i = 0
			re = 0
			for k in tab
			if (((k.to_i).is_a?(Integer)) && k.to_i != 0)
					tjNumber = tjNumber + [journa]
					break
				end
			end
		end
		puts "\tLa liste des handles contenant un numero sont au nombre de #{tjNumber.length}"
#		puts tjNumber
#		for u in tjNumber
#			print u
#			print ", "
#		end
	when 3
		# Prénom à rechercher (indépendamment de la casse)
		prenom = "aude"

		# Construire le pattern regex pour trouver le prénom à la suite
		pattern = Regexp.new(prenom, Regexp::IGNORECASE)

		# Compter le nombre d'occurrences du prénom dans les handles
		occurrences = data.count { |handle| pattern.match?(handle) }

		puts "\tLe prénom 'aude' apparaît #{occurrences} fois dans les handles."
	when 4
		pattern = /\A@[A-Z][a-z]*\z/
		nbreupcase = data.count { |handle| pattern.match?(handle) }
		puts "\t Le nombre de handle commencant par une majuscule est de #{nbreupcase}"
	when 5
		pattern = /@[A-Z]/
		nbreupcase = data.count { |handle| pattern.match?(handle) }
		puts "\t Le nombre de handle aynt au moins une majuscule est de #{nbreupcase}"
	when 6
		pattern = /_/
		#nbreupcase = data.count { |handle| pattern.match?(handle) }
		nbreupcase = data.join("").scan(/_/).count
		puts "\t Le nombre de underscore (_) dans tous les speudos confondu est de #{nbreupcase}"
	when 7
		print "\t La liste des handles triés par ordre alphabétique : "
		print data.sort()
		puts"\n"
	when 8
		shortest_handles = data.sort_by { |handle| handle.length }.take(50)

		puts "Les 50 handles les plus courts sont :"
		print shortest_handles
		puts "\n"
	when 9
		puts "la position dans l'array de la personne @epenser est #{data.index("@epenser")}"
	else
		print "Votre choix n'est pas correct !"
	end
end

def execution(data)
	print_questions()
	print "Taper le numero de la question ici : "
	choise = gets.chomp.to_i
	ask_questions(choise, data)

end

